(function() {
    /**
     * Ajuste decimal de un número.
     *
     * @param {String}  tipo  El tipo de ajuste.
     * @param {Number}  valor El numero.
     * @param {Integer} exp   El exponente (el logaritmo 10 del ajuste base).
     * @returns {Number} El valor ajustado.
     */
    function decimalAdjust(type, value, exp) {
      // Si el exp no está definido o es cero...
      if (typeof exp === 'undefined' || +exp === 0) {
        return Math[type](value);
      }
      value = +value;
      exp = +exp;
      // Si el valor no es un número o el exp no es un entero...
      if (isNaN(value) || !(typeof exp === 'number' && exp % 1 === 0)) {
        return NaN;
      }
      // Shift
      value = value.toString().split('e');
      value = Math[type](+(value[0] + 'e' + (value[1] ? (+value[1] - exp) : -exp)));
      // Shift back
      value = value.toString().split('e');
      return +(value[0] + 'e' + (value[1] ? (+value[1] + exp) : exp));
    }
  
    // Decimal round
    if (!Math.round10) {
      Math.round10 = function(value, exp) {
        return decimalAdjust('round', value, exp);
      };
    }
    // Decimal floor
    if (!Math.floor10) {
      Math.floor10 = function(value, exp) {
        return decimalAdjust('floor', value, exp);
      };
    }
    // Decimal ceil
    if (!Math.ceil10) {
      Math.ceil10 = function(value, exp) {
        return decimalAdjust('ceil', value, exp);
      };
    }
})();

exports.printMsg = function(parcial1, parcial2, trabajo, final){
    console.log('Porcentaje notas');
    
    var parcial = ((parseFloat(parcial1) + parseFloat(parcial2)) /2) * 0.25; 
    var fin = parseFloat(final) * 0.5;
    var trab = parseFloat(trabajo) * 0.25;
    var promedio = (parcial + trab + fin);
    var redon = Math.round10(promedio,-2);

    return redon;
}

exports.printParcials = function(parcial1, parcial2){
    var parcial = ((parseFloat(parcial1) + parseFloat(parcial2)) /2) * 0.25;
    var reparcial = Math.round10(parcial, -1); 
    
    return reparcial;
}

exports.printTrab = function(trabajo){
  var a = parseFloat(trabajo) * 0.25;
  var trab = Math.round10(a, -1); 
  
  return trab;
}
exports.printFin = function(final){
  var fin = parseFloat(final) * 0.5;
  
  return fin;
}

